import React, { useState } from "react";
import img from "../images/services.jpg";
import Back from "../common/Back";
import "../home/featured/Featured.css";

const Services = () => {
  const [showDescription, setShowDescription] = useState([]);

  const handleIconClick = (index) => {
    const newShowDescription = [...showDescription];
    newShowDescription[index] = !newShowDescription[index];
    setShowDescription(newShowDescription);
  };

  const servicesData = [
    {
      title: "Civil Works",
      description:
        "Includes construction, maintenance, and improvement of infrastructure projects that are essential for the functioning of a society such as roads, bridges, dams, airports, railways, canals, pipelines, and buildings.",
    },
    // Add more services as needed
    // {
    //   title: "Service Title",
    //   description: "Service Description",
    // },
  ];

  return (
    <>
      <section className="services mb">
        <Back name="Services" title="Our Services" cover={img} />
        <div className="featured container text-center">
          {servicesData.map((service, index) => (
            <div
              className={`icon-container ${showDescription[index] ? "active" : ""}`}
              key={index}
              onClick={() => handleIconClick(index)}
            >
              <img src="../images/hero/h1.png" alt={service.title} />
              <h3>{service.title}</h3>
              {showDescription[index] && (
                <div className="description">
                  <p>{service.description}</p>
                </div>
              )}
            </div>
          ))}
        </div>
      </section>
    </>
  );
};

export default Services;
